/**
 * Removing full stop from link in description
 */
UPDATE dbs.dashboard_notifications_templates SET description_En = replace(description_En, '<p class="govuk-body">To finish making your application, you must pay the application fee of ${applicationFee} as soon as possible. Your application will be paused and will not be sent to the other parties or considered by a judge until you’ve paid the fee. <a href={GA_VIEW_APPLICATION_URL} rel="noopener noreferrer" class="govuk-link">Pay application fee.</a></p>', '<p class="govuk-body">To finish making your application, you must pay the application fee of ${applicationFee} as soon as possible. Your application will be paused and will not be sent to the other parties or considered by a judge until you’ve paid the fee. <a href={GA_VIEW_APPLICATION_URL} rel="noopener noreferrer" class="govuk-link">Pay application fee</a></p>')
                                             WHERE template_name in ('Notice.AAA6.GeneralApps.ApplicationFeeRequired.Applicant');
UPDATE dbs.dashboard_notifications_templates SET description_Cy = replace(description_Cy, '<p class="govuk-body">I orffen gwneud eich cais, rhaid i chi dalu’r ffi gwneud cais o ${applicationFee} cyn gynted â phosib. Bydd eich cais yn cael ei oedi ac ni chaiff ei anfon at y partïon eraill na’i ystyried gan farnwr nes i chi dalu’r ffi. <a href={GA_VIEW_APPLICATION_URL} rel="noopener noreferrer" class="govuk-link">Talu’r ffi gwneud cais.</a></p>', '<p class="govuk-body">I orffen gwneud eich cais, rhaid i chi dalu’r ffi gwneud cais o ${applicationFee} cyn gynted â phosib. Bydd eich cais yn cael ei oedi ac ni chaiff ei anfon at y partïon eraill na’i ystyried gan farnwr nes i chi dalu’r ffi. <a href={GA_VIEW_APPLICATION_URL} rel="noopener noreferrer" class="govuk-link">Talu’r ffi gwneud cais</a></p>')
                                             WHERE template_name in ('Notice.AAA6.GeneralApps.ApplicationFeeRequired.Applicant');
